import React from 'react'
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  // useSubscription,
  // useMutation,
  gql
} from '@apollo/client'

const client = new ApolloClient({
  uri: 'http://localhost:4000/',
  cache: new InMemoryCache()
})

const getMessages = gql`
  query {
  messages {
    id
    content
    user
  }
}
`

const Messages = ({ user }) => {
  const { data } = useQuery(getMessages)
  if (!data) {
    return null
  }
  return (
    <>
      {data.messages.map(({ id, user: messageUser, content }) => (
        <div
          key={id}
          style={{
            display: "flex",
            justifyContent: user === messageUser ? "flex-end" : "flex-start",
            paddingBottom: "1em",
          }}
        >
          {user !== messageUser && (
            <div
              style={{
                height: 50,
                width: 50,
                marginRight: "0.5em",
                border: "2px solid #e5e6ea",
                borderRadius: 25,
                textAlign: "center",
                fontSize: "18pt",
                paddingTop: 5,
              }}
            >
              {messageUser.slice(0, 2).toUpperCase()}
            </div>
          )}
          <div
            style={{
              background: user === messageUser ? "blue" : "#e5e6ea",
              color: user === messageUser ? "white" : "black",
              padding: "1em",
              borderRadius: "1em",
              maxWidth: "60%",
            }}
          >
            {content}
          </div>
        </div>
      ))}
    </>
  )
}

const Chat = () => {
  const [state, stateSet] = React.useState({
    user: 'Jack',
    content: ''
  })
  return (
    // <div class='content is-justify-content-flex-end' style={{ background: 'red', width: '70%' }}>
    //
    // </div>
    <div class="features">
      <div class="container">
        <div class="columns is-centered is-vcentered is-mobile">
          <div class="column is-narrow has-text-centered" style={{ background: 'red', width: '80%' }}>
            <Messages class='content is-justify-content-flex-end' user={state.user} />
          </div>
        </div>
      </div>
    </div>
  )
}

const provider = () => {
  return (
    <ApolloProvider client={client}>
      <Chat />
      <div class="columns">
        <div class="column">
          First column
        </div>
        <div class="column">
          Second column
        </div>
        <div class="column">
          Third column
        </div>
        <div class="column">
          Fourth column
        </div>
      </div>
    </ApolloProvider>
  )
}

export default provider
