import React from 'react'
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  // useSubscription,
  useMutation,
  gql
} from '@apollo/client'

const client = new ApolloClient({
  uri: 'http://localhost:4000/',
  cache: new InMemoryCache()
})

const getMessages = gql`
  query {
  messages {
    id
    content
    user
  }
}
`

const Messages = ({ user }) => {
  const { data } = useQuery(getMessages)
  if (!data) {
    return null
  }
  return (
    <>
      {data.messages.map(({ id, user: messageUser, content }) => (
        <div
          key={id}
          style={{
            display: 'flex',
            justifyContent: user === messageUser ? 'flex-end' : 'flex-start',
            paddingBottom: '1em',
          }}
        >
          {user !== messageUser && (
            <div
              style={{
                height: 50,
                width: 50,
                marginRight: '0.5em',
                border: '2px solid #e5e6ea',
                borderRadius: 25,
                textAlign: 'center',
                fontSize: '18pt',
                paddingTop: 5
              }}
            >
              {messageUser.slice(0, 2).toUpperCase()}
            </div>
          )}
          <div
            style={{
              background: user === messageUser ? 'blue' : '#e5e6ea',
              color: user === messageUser ? 'white' : 'black',
              padding: '1em',
              borderRadius: '1em',
              maxWidth: '60%'
            }}
          >
            {content}
          </div>
        </div>
      ))}
    </>
  )
}

const Chat = () => {
  const [state, stateSet] = React.useState({
    user: 'Jack',
    content: ''
  })

  const sendMessage = () => {
    if (state.content.length > 0) {

    }
    stateSet({
      ...state,
      content: ''
    })
  }
  return (
    <div>
      <Messages class='content is-justify-content-flex-end' user={state.user} />
      <div class='columns'>
        <div class='column is-2'>
          <input
            class='input is-success' type='text' placeholder='Usuario' value={state.user} onChange={(evt) => stateSet({
              ...state,
              user: evt.target.valude
            })}
          />
        </div>
        <div class='column is-8'>
          <input
            class='input is-info' type='text' placeholder='Mensaje' value={state.content} onChange={(evt) => stateSet({
              ...state,
              content: evt.target.valude
            })}
            onKeyUp={(evt) => {
              if (evt.keyCode === 13) {
                sendMessage()
              }
            }}
          />
        </div>
        <div class='column is-2'>
          <button class='button is-link is-light' onClick={() => sendMessage()}>Enviar</button>
        </div>
      </div>
    </div>
    // </div>
  )
}

const provider = () => {
  return (
    <ApolloProvider client={client}>
      <div class='features'>
        <div class='container'>
          <div class='columns is-centered is-vcentered is-mobile'>
            <div class='column is-narrow has-text-centered' style={{ width: '80%' }}>
              <Chat />
            </div>
          </div>
        </div>
      </div>
    </ApolloProvider>
  )
}

export default provider
