import React from 'react'
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useSubscription,
  useMutation,
  gql
} from '@apollo/client'
import { WebSocketLink } from '@apollo/client/link/ws'

const link = new WebSocketLink({
  uri: 'ws://localhost:4000/',
  options: {
    reconnect: true
  }
})
const client = new ApolloClient({
  uri: 'http://localhost:4000/',
  cache: new InMemoryCache(),
  link
})

const getMessages = gql`
  subscription {
  messages {
    id
    content
    user
  }
}
`

const postMessages = gql`
  mutation ($user: String!, $content:String!) {
    postMessage(user: $user, content: $content)
  }
`

const Messages = ({ user }) => {
  const { data } = useSubscription(getMessages)
  if (!data) {
    return null
  }

  return (
    <>
      {data.messages.map(({ id, user: messageUser, content }) => (
        <div key={id}>
          <div
            style={{
              marginRight: '0.5em',
              textAlign: 'left',
              fontSize: '12pt',
              paddingTop: 5
            }}
          >
            {messageUser}
          </div>
          <div
            key={id}
            style={{
              display: 'flex',
              justifyContent: user === messageUser ? 'flex-end' : 'flex-start',
              paddingBottom: '1em'
            }}
          >
            <div
              style={{
                background: user === messageUser ? 'blue' : '#e5e6ea',
                color: user === messageUser ? 'white' : 'black',
                padding: '1em',
                borderRadius: '1em',
                maxWidth: '60%'
              }}
            >
              {content}
            </div>
          </div>
        </div>
      ))}
    </>
  )
}

const Chat = () => {
  const [state, stateSet] = React.useState({
    user: '',
    content: ''
  })

  const [pstMessages] = useMutation(postMessages)
  const sendMessage = () => {
    if (state.content.trim().length > 0) {
      pstMessages({
        variables: state
      })
    }
    stateSet({
      ...state,
      content: ''
    })
  }
  return (
    <div>
      <Messages class='content is-justify-content-flex-end' user={state.user} />
      <div class='columns'>
        <div class='column is-2'>
          <input
            class='input is-success' type='text' placeholder='Usuario' value={state.user} onChange={(evt) => stateSet({
              ...state,
              user: evt.target.value
            })}
          />
        </div>
        <div class='column is-8'>
          <input
            class='input is-info' type='text' placeholder='Mensaje' disabled={state.user.trim().length === 0} value={state.content} onChange={(evt) => stateSet({
              ...state,
              content: evt.target.value
            })}
            onKeyUp={(evt) => {
              if (evt.keyCode === 13) {
                sendMessage()
              }
            }}
          />
        </div>
        <div class='column is-2'>
          <button class='button is-link is-light' disabled={state.user.trim().length === 0} onClick={() => sendMessage()}>Enviar</button>
        </div>
      </div>
    </div>
    // </div>
  )
}

const provider = () => {
  return (
    <ApolloProvider client={client}>
      <div class='features'>
        <div class='container'>
          <div class='columns is-centered is-vcentered is-mobile'>
            <div class='column is-narrow has-text-centered' style={{ width: '80%' }}>
              <Chat />
            </div>
          </div>
        </div>
      </div>
    </ApolloProvider>
  )
}

export default provider
