import React, { useRef, useEffect } from 'react'
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useSubscription,
  useMutation,
  gql
} from '@apollo/client'
import { WebSocketLink } from '@apollo/client/link/ws'

const link = new WebSocketLink({
  uri: 'ws://localhost:4000/',
  options: {
    reconnect: true
  }
})
const client = new ApolloClient({
  uri: 'http://localhost:4000/',
  cache: new InMemoryCache(),
  link
})

const getMessages = gql`
  subscription {
  messages {
    id
    content
    user
  }
}
`

const postMessages = gql`
  mutation ($user: String!, $content:String!) {
    postMessage(user: $user, content: $content)
  }
`

const Messages = ({ user }) => {
  const { data } = useSubscription(getMessages)
  if (!data) {
    return null
  }

  return (
    <>
      {data.messages.map(({ id, user: messageUser, content }) => (
        <div key={id}>
          <div
            style={Object.assign({}, styles.messageAuthor, { textAlign: user === messageUser ? 'right' : 'left' })}
          >
            {messageUser}
          </div>
          <div
            style={{
              display: 'flex',
              justifyContent: user === messageUser ? 'flex-end' : 'flex-start',
              paddingBottom: '1em'
            }}
          >
            <div
              style={Object.assign({}, styles.messageContent, (user === messageUser) ? styles.transmitter : styles.receiver)}
            >
              {content}
            </div>
          </div>
        </div>
      ))}
    </>
  )
}

const Chat = () => {
  const [state, stateSet] = React.useState({
    user: '',
    content: ''
  })

  const [pstMessages] = useMutation(postMessages)
  const sendMessage = () => {
    if (state.content.trim().length > 0) {
      pstMessages({
        variables: state
      })
    }
    stateSet({
      ...state,
      content: ''
    })
  }

  const divRef = useRef(null)
  useEffect(() => {
    divRef.current.scrollIntoView({ behavior: 'smooth' })
  })

  return (
    <div>
      <div style={{ paddingBottom: '55px' }}>
        <Messages user={state.user} />
      </div>
      <div ref={divRef} />
      <div
        class='columns is-flex' style={styles.bottomContainer}
      >
        <div class='column is-10'>
          <input
            class='input is-info'
            type='text'
            placeholder='Mensaje'
            disabled={state.user.trim().length === 0}
            value={state.content} onChange={(evt) => stateSet({
              ...state,
              content: evt.target.value
            })}
            onKeyUp={(evt) => {
              if (evt.keyCode === 13) {
                sendMessage()
              }
            }}
          />
        </div>
        <div class='column is-2'>
          <button
            class='button is-link is-light'
            disabled={state.user.trim().length === 0}
            onClick={() => sendMessage()}
          >
            Enviar
          </button>
        </div>
      </div>
    </div>
  )
}

const UserForm = () => {
  const [state] = React.useState({
    user: '',
    content: ''
  })
  return (
    <div>
      <div class='field'>
        <label class='label '>Username</label>
        <div class="field is-grouped">
          <div class='control'>
            <input class='input is-success' type='text' placeholder='Username' />
          </div>
          <div class="control">
            <button class="button is-link">Submit</button>
          </div>
        </div>
      </div>
    </div>
  )
}

const Provider = () => {
  // const [state] = React.useState()
  // const [state] = React.useState({
  //   user: '',
  //   content: ''
  // })
  return (
    <ApolloProvider client={client}>
      <div class='features'>
        <div class='container'>
          <div class='columns is-centered is-vcentered is-mobile'>
            <div
              class='column is-narrow has-text-centered'
              style={{ width: '80%' }}
            >
              {/* <Chat /> */}
              <UserForm />
            </div>
          </div>
        </div>
      </div>
    </ApolloProvider>
  )
}

const styles = {
  messageAuthor: {
    marginRight: '0.5em',
    fontSize: '12pt',
    paddingTop: 5
  },

  messageContent: {
    padding: '18px',
    borderRadius: '10px',
    maxWidth: '80%'
  },

  transmitter: {
    background: 'blue',
    color: 'white'
  },

  receiver: {
    background: '#95F0B8',
    color: 'black'
  },

  bottomContainer: {
    marginTop: '1rem',
    padding: '1rem',
    position: 'fixed',
    bottom: 0,
    left: 0,
    width: '100%',
    backgroundColor: 'white'
  }
}

export default Provider
