import React from 'react'
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  // useSubscription,
  // useMutation,
  gql
} from '@apollo/client'

const client = new ApolloClient({
  uri: 'http://localhost:4000/',
  cache: new InMemoryCache()
})

const getMessages = gql`
  query {
  messages {
    id
    content
    user
  }
}
`

const Messages = ({ user }) => {
  const { data } = useQuery(getMessages)
  if (!data) {
    return null
  }
  return JSON.stringify(data)
}

const Chat = () => {
  return (
    <div>
      <Messages user='Jack' />
    </div>
  )
}

const provider = () => {
  return (
    <ApolloProvider client={client}>
      <Chat />
    </ApolloProvider>
  )
}

export default provider
